$(document).ready(function() {
    // Function to show items based on category
    function showItems(category) {
      // Hide all tab panes first
      $('#portfolioTabContent .tab-pane').removeClass('show active');
      // Show the selected tab pane
      $('#' + category).addClass('show active');
  
      // Update active class in portfolio filters
      $('#portfolioTab li .nav-link').removeClass('active');
      $('#portfolioTab li a[aria-controls="' + category + '"]').addClass('active');
  
      // Filter items based on data-category attribute
      if (category === 'all') {
        // Show all items
        $('[data-category]').parent().show();
      } else {
        // Show only items with matching data-category attribute
        $('[data-category]').parent().hide();
        $('[data-category="' + category + '"]').parent().show();
      }
    }
  
    // Handle tab click event
    $('#portfolioTab a').on('click', function (e) {
      e.preventDefault();
      var category = $(this).attr('aria-controls'); // Get the target tab ID
      showItems(category); // Call function to show items based on category
    });
  
    // Initial display: Show all items
    showItems('all');
  });
  